jQuery(document).ready(function ($) {
	'use strict';

	$('.ai1m-purchase-add').click(function (e) {
		var self = $(this);

		self.attr('disabled', true);

		var dialog = self.closest('.ai1m-modal-dialog');
		var error = dialog.find('.ai1m-modal-error');
		var index = dialog.attr('id').split('-').pop();
		var purchaseId = dialog.find('.ai1m-purchase-id').val();
		var updateLink = dialog.find('.ai1m-update-link').val();

		// Check Purchase ID
		$.ajax({
			url: 'https://servmask.com/purchase/' + purchaseId + '/check',
			type: 'GET',
			dataType: 'json',
			dataFilter: function dataFilter(data, type) {
				return Ai1m.Util.json(data);
			}
		}).done(function (product) {
			// Update Purchase ID
			$.ajax({
				url: ai1m_updater.ajax.url,
				type: 'POST',
				dataType: 'json',
				data: {
					'ai1m_uuid': product.uuid,
					'ai1m_extension': product.extension
				},
				dataFilter: function dataFilter(data, type) {
					return Ai1m.Util.json(data);
				}
			}).done(function () {
				window.location.hash = '';

				// Update plugin row
				$('#ai1m-update-section-' + index).html($('<a />').attr('href', updateLink).text(ai1m_locale.check_for_updates));

				self.attr('disabled', false);
			});
		}).fail(function () {
			self.attr('disabled', false);
			error.html(ai1m_locale.invalid_purchase_id);
		});

		e.preventDefault();
	});

	$('.ai1m-purchase-discard').click(function (e) {
		window.location.hash = '';

		e.preventDefault();
	});
});

